import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
  registerForm!: FormGroup;

  constructor(private formBuilder: FormBuilder) { }

  ngOnInit() {
    this.registerForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', Validators.required]
    });
  }

  register() {
    if (this.registerForm.invalid) {
      return;
    }

    // Aquí puedes enviar los datos del formulario al servidor para registrar al usuario
    console.log(this.registerForm.value);
  }
}
<form [formGroup]="registerForm" (ngSubmit)="register()" class="formulario__registro">
  <div>
    <label>Nombre</label>
    <input type="text" formControlName="firstName" required>
    <div *ngIf="registerForm.get('firstName').invalid && registerForm.get('firstName').touched">
      <span *ngIf="registerForm.get('firstName').errors.required">El nombre es requerido.</span>
    </div>
  </div>

  <div>
    <label>Apellido</label>
    <input type="text" formControlName="lastName" required>
    <div *ngIf="registerForm.get('lastName').invalid && registerForm.get('lastName').touched">
      <span *ngIf="registerForm.get('lastName').errors.required">El apellido es requerido.</span>
    </div>
  </div>

  <div>
    <label>Correo Electrónico</label>
    <input type="email" formControlName="email" required>
    <div *ngIf="registerForm.get('email').invalid && registerForm.get('email').touched">
      <span *ngIf="registerForm.get('email').errors.required">El correo electrónico es requerido.</span>
      <span *ngIf="registerForm.get('email').errors.email">El correo electrónico no es válido.</span>
    </div>
  </div>

  <div>
    <label>Contraseña</label>
    <input type="password" formControlName="password" required>
    <div *ngIf="registerForm.get('password').invalid && registerForm.get('password').touched">
      <span *ngIf="registerForm.get('password').errors.required">La contraseña es requerida.</span>
      <span *ngIf="registerForm.get('password').errors.minlength">La contraseña debe tener al menos 6 caracteres.</span>
    </div>
  </div>

  <div>
    <label>Confirmar Contraseña</label>
    <input type="password" formControlName="confirmPassword" required>
    <div *ngIf="registerForm.get('confirmPassword').invalid && registerForm.get('confirmPassword').touched">
      <span *ngIf="registerForm.get('confirmPassword').errors.required">Debes confirmar la contraseña.</span>
    </div>
  </div>

  <button type="submit" [disabled]="registerForm.invalid">Registrarse</button>
</form>
